import { motion } from 'framer-motion'
import React from 'react'
import styled from 'styled-components'
import Logo from '../assets/Svgs/star_white_48dp.svg'

const Section = styled.section`
	min-height: 100vh;
	width: 100vw;
	margin: 5rem auto;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	background-color: ${props => props.theme.body};
	color: ${props => props.theme.text};
	position: relative;
`
const LogoContainer = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	img {
		width: 10vw;
		height: auto;
	}
	h3 {
		font-size: ${props => props.theme.fontxl};
		font-family: "Kaushan Script";
	}
`
const FooterComponent = styled(motion.footer)`
	width: 80vw;
	ul {
		list-style: none;
		display: flex;
		justify-content: space-between;
		align-items: center;
		flex-wrap: wrap;
		margin: 2rem;
		margin-top: 4rem;
		border-top: 1px solid ${props => props.theme.text};
		border-bottom: 1px solid ${props => props.theme.text};
	}
	li {
		font-size: ${props => props.theme.fontlg};
		padding: 2rem;
		text-transform: uppercase;
		cursor: pointer;
		transition: all 0.3s ease;
		&:hover {
			transform: scale(1.1);
		}
	}
`
const Bottom = styled.div`
	padding: 0.5rem 0;
	margin: 0 4rem;
	font-size: ${props => props.theme.fontlg};
	display: flex;
	align-items: center;
	justify-content: space-between;
`
const Footer = () => {
  return (
    <Section>
      <LogoContainer>
        <img data-scroll data-scroll-speed='2' src={Logo} alt='wibe-studio' />
        <h3data-scroll data-scroll-speed='-1' data-scroll-direction='horizontal'>
					Wibe Studio
				</h3data-scroll>
      </LogoContainer>
      <FooterComponent>
        <ul>
          <li>home</li>
          <li>about</li>
          <li>shop</li>
          <li>new arrival</li>
          <li>
            <a href='#'>look book</a>
          </li>
          <li>
            <a href='#'>reviews</a>
          </li>
        </ul>
        <Bottom>
          <span data-scroll data-scroll-speed='-2' data-scroll-direction='horizontal'>
						&copy;{new Date().getFullYear()}. All Rights Reserved.
					</span>
          <span data-scroll data-scroll-speed='-2' data-scroll-direction='horizontal'>
						Made with &hearts; by Dhanraj Choudhary
					</span>
        </Bottom>
      </FooterComponent>
    </Section>
  )
}

export default Footer
