import React from 'react'
import styled from 'styled-components'

const Section = styled.section`
	min-height: 100vh;
	width: 100vw;
	margin: 0 auto;
	display: flex;
	justify-content: center;
	align-items: center;
	position: relative;
`
const Overlay = styled.div`
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	width: 30vw;
	height: 90vh;
	box-shadow: 0 0 0 5vw ${props => props.theme.text};
	border: 3px solid black;
	z-index: 11;
`
const Title = styled.h1`
	font-size: ${props => props.theme.fontxxxl};
	font-family: 'Kaushan Script';
	font-weight: 300;
	/* text-transform: capitalize; */
	color: ${props => props.theme.body};
	text-shadow: 1px 1px 1px ${props => props.theme.text};
	position: absolute;
	top: 2rem;
	left: 1rem;
	z-index: 15;
`
const Text = styled.div`
	width: 20%;
	font-size: ${props => props.theme.fontlg};
	font-weight: 300;
	position: absolute;
	padding: 2rem;
	top: 0;
	right: 0;
	z-index: 11;
`
const Container = styled.div`
	position: absolute;
	top: 0;
	left: 50%;
	transform: translate(-50%, 0);
	width: 25vw;
	height: auto;
	display: flex;
	justify-content: center;
	flex-direction: column;
	align-items: center;
`

const Item = styled(motion.div)`
	display: flex;
	width: 20rem;
	margin-right: 6rem;
	justify-content: center;
	align-items: center;
	flex-direction: column;
	img {
		width: 100%;
		height: auto;
		cursor: pointer;
	}
	h1 {
		font-weight: 500;
		width: fit-content;
		cursor: pointer;
		text-align: center;
		display: inline-block;
	}
`
const Product = ({ img, title = '' }) => {
  return (
    <Item
      initial={{
        filter: 'grayscale(100%)'
      }}
      whileInView={{
        filter: 'grayscale(0%)'
      }}
      transition={{
        duration: '0.5'
      }}
      viewport={{ once: false, amount: 'all' }}
		>
      <img src={img} alt={title} />
      <h1>
        {title}
      </h1>
    </Item>
  )
}

const NewArrival = () => {
  return (
    <Section>
      <Overlay />
      <Title data-scroll data-scroll-direction='horizontal' data-scroll-speed='-2'>
				New Arrivals
			</Title>
      <Text data-scroll data-scroll-speed='-4'>
				There is new collection available for cool clothes in all sizes. This collection is a great way to find a new
				look for you. It offers a variety of cool apparel styles to fit your taste, while you can also find some cool
				clothes that you can wear everyday.
				<br />
        <br />
				The first line of clothing you will see on this collection is for men. The collection also includes three new
				styles for women.
				<br />
        <br />
				Give it a try and experience a new look.
			</Text>
      <Container ref={horizontalRef}>
        <Product img={img3} title='Sweatshirts' />
        <Product img={img4} title='Ethnic Wear' />
        <Product img={img1} title='Man Basics' />
        <Product img={img2} title='Tops' />
        <Product img={img5} title='Blazers' />
        <Product img={img6} title='Suits' />
        <Product img={img7} title='Antiques' />
        <Product img={img8} title='Jewellery' />
        <Product img={img9} title='Watches' />
        <Product img={img10} title='Special Edition' />
      </Container>
    </Section>
  )
}

export default NewArrival
