import React from 'react'
import styled from 'styled-components'

const Section = styled(motion.section)`
  min-height: 100vh;
  height: auto;
  /* width: 80vw; */
  width: 100%;
  margin: 0 auto;
  overflow: hidden;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  position: relative;
  /* background-color: orange; */
`

const Title = styled.h1`
	font-size: ${props => props.theme.fontxxxl};
	font-family: 'Kaushan Script';
	font-weight: 300;
	position: absolute;
	top: 1rem;
	color: ${props => props.theme.text};
	left: 5%;
	z-index: 11;
`
const Left = styled.div`
	width: 35%;
	min-height: 100vh;
	background-color: ${props => props.theme.body};
	color: ${props => props.theme.text};
	z-index: 5;
	position: fixed;
	display: flex;
	justify-content: center;
	align-items: center;
	left: 0;
	p {
		font-size: ${props => props.theme.fontlg};
		font-weight: 300;
		width: 80%;
		margin: 0 auto;
	}
`
const Shop = () => {
  return (
    <Section>
      <Title data-scroll data-scroll-speed='-1'>
				New Arrival
			</Title>
      <Left>
        <p>
					The brand new collection is currently being developed in USA. We create our products using best quality
					material, including the use of some of the pure fabrics to make our products. All products are made using the
					best materials, from the finest cotton to the finest fabrics.
					<br />
          <br />
					We have lots of different clothing options like shoes, jackets and dresses. Not only clothes but we also
					provide unique Jewellery as well. It is great for us to carry our new clothes all around the country and look
					different.
				</p>
      </Left>
    </Section>
  )
}

export default Shop
